import { Redirect } from "react-router";

const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route
        {...rest}
        render={
            (props) => {
                return localStorage.getItem("token") ? (<Component {...props} />) : (<Redirect to="/login" />)
            }
        }
    />
);

export default PrivateRoute;
